sys_graphql_resolver.script:
(function process( /* ResolverEnvironment */ env) {
    try {
        // Get the 'name' argument from the GraphQL query
        var breedName = env.getArguments().name;
        var normalizedBreed = breedName.toLowerCase().replace(/\s+/g, '');
        var imageMsg = new sn_ws.RESTMessageV2();
        imageMsg.setEndpoint("https://dog.ceo/api/breed/" + normalizedBreed + "/images/random");
        imageMsg.setHttpMethod("GET");
        var imageResponse = imageMsg.execute();
        var imageData = JSON.parse(imageResponse.getBody());
        return {
            name: breedName,
            image: imageData.message
        };
    } catch (e) {
        gs.error("getBreed error: " + e.message);
        return {
            name: breedName || "",
            image: null
        };
    }
})(env);
