sys_graphql_resolver.script:
(function process( /*ResolverEnvironment*/ env) {

    var nameGen = new DogNameGenerator();
    var gr = new GlideRecord('u_animals');
    gr.addNullQuery('u_name');
    gr.query();

    var updated = 0;

    while (gr.next()) {
        gr.u_name = nameGen.getNextName();
        gr.update();
        updated++;
    }

    return {
        message: "ğŸ¾ Assigned " + updated + " dog names."
    };
})(env);
