sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
//Use this function to grab records from the Events table using
//the Look up Event records data resource, then convert the
//record data into a JSON format that the Calendar component requires.
const eventArray = api.data.look_up_event_records.results;
var calendarArray = [];
for (var i = 0; i < eventArray.length; i++) {
//console.log("event DR object = " + JSON.stringify(eventArray[i], null, 4));
const calObj = {
'id': eventArray[i]._row_data.uniqueValue,
'title': eventArray[i].event_name.displayValue,
'description': eventArray[i].short_description.displayValue,
'start': eventArray[i].start_date_time.displayValue,
'end': eventArray[i].end_date_time.displayValue
};
calendarArray.push(calObj);
//console.log("calObj = " + JSON.stringify(calObj, null, 4));
}
return calendarArray;
}
