sys_ux_client_script.script:
function handler({
    api,
    event,
    helpers,
    imports
}) {

    //If EVENT is 'Grid Clicked for new Event', use these payload values
    //these payload values relate to the date/time of the current day
    var startVar = event.payload.newStartMS;
    var endVar = event.payload.newEndMS;

    //If EVENT is 'Grid double clicked', use these payload values
    //these payload values relate to the date/time of the grid clicked on
    if (startVar == null && endVar == null) {
        startVar = event.payload.startTimeMS;
        endVar = event.payload.endTimeMS;
    }
    var sDate = new Date(startVar);
    var eDate = new Date(endVar);

    //Format the date as: yyyy-MM-dd HH:mm:ss
    var formatStartDate = sDate.getFullYear() + '-' +
        ('0' + (sDate.getMonth() + 1)).slice(-2) + '-' +
        ('0' + sDate.getDate()).slice(-2) + ' ' +
        ('0' + sDate.getHours()).slice(-2) + ':' +
        ('0' + sDate.getMinutes()).slice(-2) + ':' +
        ('0' + sDate.getSeconds()).slice(-2);
    var formatEndDate = eDate.getFullYear() + '-' +
        ('0' + (eDate.getMonth() + 1)).slice(-2) + '-' +
        ('0' + eDate.getDate()).slice(-2) + ' ' +
        ('0' + eDate.getHours()).slice(-2) + ':' +
        ('0' + eDate.getMinutes()).slice(-2) + ':' +
        ('0' + eDate.getSeconds()).slice(-2);
    //update the client state parameters with these default date/times
    api.setState("eventStartDT", formatStartDate);
    api.setState("eventEndDT", formatEndDate);
}
