sys_ux_data_broker_transform.script:
function transform(input) {
var theTable = input.table;
var theName = input.eventName;
var theStart = new GlideDateTime(input.eventStart);
var theEnd = new GlideDateTime(input.eventEnd);
var theShortDesc = input.shortDesc;
var eventGR = new GlideRecordSecure(theTable);
eventGR.setValue("event_name", theName);
eventGR.setValue("start_date_time", theStart);
eventGR.setValue("end_date_time", theEnd);
eventGR.setValue("short_description", theShortDesc);
eventGR.insert();
}
